// src/api.ts

import axios from 'axios';

const BASE_URL = 'http://your-api-url.com/api'; // Replace with your actual API URL

export const api = {
  getMedicines: async () => {
    const response = await axios.get(`${BASE_URL}/medicines`);
    return response.data; // Return the array of medicines
  },

  updateMedicineQuantity: async (id: number, quantity: number) => {
    await axios.patch(`${BASE_URL}/medicines/${id}`, { quantity });
  },
};
