import React, { useState } from 'react';
import { FileText, Download } from 'lucide-react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

// Define Sale interface to structure sales data
interface Sale {
  itemName: string;
  quantitySold: number;
  amount: number;
}

// Sample sales data (replace with real data from your system)
const completedSales: Sale[] = [
  { itemName: 'Paracetamol', quantitySold: 5, amount: 50 },
  { itemName: 'Ibuprofen', quantitySold: 3, amount: 45 },
  { itemName: 'Amoxicillin', quantitySold: 10, amount: 200 },
];

const Reports: React.FC = () => {
  const [reportType, setReportType] = useState<string>('sales');
  const [dateRange, setDateRange] = useState<string>('daily');
  const [reportContent, setReportContent] = useState<string>('');

  const handleGenerateReport = () => {
    let generatedReport = '';

    switch (reportType) {
      case 'inventory':
        generatedReport = 'Inventory Report: \nMedicine details (Name, Batch Number, Expiry Date, Price, Quantity)';
        break;
      case 'employee':
        generatedReport = 'Employee Collection Report: \nEmployee Name, Number of Transactions, Date, Amount Collected';
        break;
      case 'dispensed':
        generatedReport = 'Dispensed Medication Report: \nItem Name, Initial Balance, Sold Quantity, Remaining Quantity';
        break;
      case 'sales':
        // Generate the sales report based on completedSales array
        let totalAmount = 0;
        generatedReport = 'Sales Report:\n\n';

        // Loop through completed sales and format report
        completedSales.forEach((sale) => {
          generatedReport += `Item Name: ${sale.itemName}, Quantity Sold: ${sale.quantitySold}, Amount: $${sale.amount}\n`;
          totalAmount += sale.amount;
        });

        // Add total amount to the report
        generatedReport += `\nTotal Amount: $${totalAmount}`;
        break;
      default:
        generatedReport = 'Sales Report: \nSales data will be displayed here';
    }

    setReportContent(generatedReport);
    console.log('Generating report', { reportType, dateRange });
  };

  const handleExportPDF = () => {
    const doc = new jsPDF();
    doc.text('Report', 10, 10);
    doc.autoTable({ html: '#report-preview' });
    doc.save('report.pdf');
    console.log('Exporting to PDF');
  };

  const handleExportExcel = () => {
    // Logic for exporting to Excel
    console.log('Exporting to Excel');
  };

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Reports</h1>
      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="report-type">
            Report Type
          </label>
          <select
            id="report-type"
            value={reportType}
            onChange={(e) => setReportType(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="sales">Sales Report</option>
            <option value="inventory">Inventory Report</option>
            <option value="employee">Employee Collection Report</option>
            <option value="dispensed">Dispensed Medicine Report</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="date-range">
            Date Range
          </label>
          <select
            id="date-range"
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
            <option value="custom">Custom</option>
          </select>
        </div>
        <div className="flex items-center justify-between">
          <button
            onClick={handleGenerateReport}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            <FileText className="inline-block mr-2" />
            Generate Report
          </button>
        </div>
      </div>

      {/* Report Preview Section */}
      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <h2 className="text-xl font-bold mb-4">Report Preview</h2>
        <pre id="report-preview" className="text-gray-600 whitespace-pre-wrap">{reportContent || 'Generated report will be displayed here.'}</pre>
      </div>

      {/* Export Buttons Below the Report Preview */}
      <div className="flex items-center justify-between">
        <button
          onClick={handleExportPDF}
          className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-2"
        >
          <Download className="inline-block mr-2" />
          Export PDF
        </button>
        <button
          onClick={handleExportExcel}
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          <Download className="inline-block mr-2" />
          Export Excel
        </button>
      </div>
    </div>
  );
};

export default Reports;
