const handlePrintReceipt = () => {
  const doc = new jsPDF();

  // Get current date and time
  const currentDate = new Date();
  const formattedDate = currentDate.toLocaleString('en-US', {
    year: 'numeric',
    month: 'numeric',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    second: 'numeric',
    hour12: true,
  });

  // Title
  doc.text('Receipt', 14, 20);
  doc.text(`Date: ${formattedDate}`, 14, 30);
  doc.text('Items:', 14, 40);

  let y = 50; // Initial Y position for items

  if (cart.length === 0) {
    doc.text('No items in cart.', 14, y);
  } else {
    // Loop through the cart and display each item in the receipt
    cart.forEach(item => {
      doc.text(`${item.name} x ${item.quantity}: Tsh.${item.total.toFixed(2)}`, 14, y);
      y += 10;
    });

    y += 10; // Space after items

    // Calculate total
    const total = cart.reduce((sum, item) => sum + item.total, 0);
    doc.text(`Total: Tsh.${total.toFixed(2)}`, 14, y);

    y += 10; // Space after total

    // Payment Method
    doc.text(`Payment Method: ${paymentMethod}`, 14, y);

    y += 20; // Space before the medicine dose

    // Medicine Dose Section
    doc.text('Medicine Dose:', 14, y);
    y += 10;
    doc.line(14, y, 190, y); // Draw a line for the dose explanation (optional)

    y += 20; // Space for the final message

    // Thank you message
    doc.text('Welcome back', 14, y);
  }

  // Save the generated PDF
  doc.save('receipt.pdf');
};
