import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash, CheckCircle, XCircle } from 'lucide-react';
import { api } from '../api'; // Import your API helper for database communication
import { Medicine } from '../types'; // Import Medicine type

const MedicineManagement: React.FC = () => {
  // State to store list of medicines
  const [medicines, setMedicines] = useState<Medicine[]>([]);

  // State to manage adding or editing medicine
  const [isAddingMedicine, setIsAddingMedicine] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  // State to manage new or editing medicine form
  const [newMedicine, setNewMedicine] = useState<Omit<Medicine, 'id'>>({
    name: '',
    manufacturer: '',
    expiryDate: '',
    price: 0,
    quantity: 0
  });

  // State to store the medicine being edited
  const [editingMedicine, setEditingMedicine] = useState<Medicine | null>(null);

  // State to manage error/success messages
  const [message, setMessage] = useState<{ type: 'success' | 'error'; content: string } | null>(null);

  // Fetch medicines from the database on component mount
  useEffect(() => {
    loadMedicines(); // Load medicines when component mounts
  }, []);

  // Function to load all medicines from the database
  const loadMedicines = async () => {
    try {
      const data = await api.getMedicines();
      setMedicines(data); // Set medicines in state
      showMessage('success', 'Medicines loaded successfully');
    } catch (err) {
      showMessage('error', 'Failed to load medicines');
    }
  };

  // Function to add new medicine to the database
  const handleAddMedicine = async () => {
    try {
      await api.addMedicine(newMedicine); // Call API to add medicine
      await loadMedicines(); // Reload medicines
      resetForm(); // Reset the form after adding
      showMessage('success', 'Medicine added successfully');
    } catch (err) {
      showMessage('error', 'Failed to add medicine');
    }
  };

  // Function to prepare the form for editing a selected medicine
  const handleEditMedicine = (medicine: Medicine) => {
    setEditingMedicine(medicine); // Set the selected medicine for editing
    setNewMedicine({ ...medicine }); // Populate form with selected medicine data
    setIsEditing(true); // Toggle editing mode
  };

  // Function to update an existing medicine in the database
  const handleUpdateMedicine = async () => {
    if (editingMedicine) {
      try {
        await api.updateMedicine(editingMedicine.id, newMedicine); // Call API to update medicine
        await loadMedicines(); // Reload medicines
        resetForm(); // Reset the form after updating
        showMessage('success', 'Medicine updated successfully');
      } catch (err) {
        showMessage('error', 'Failed to update medicine');
      }
    }
  };

  // Function to delete a medicine from the database
  const handleDeleteMedicine = async (id: number) => {
    try {
      await api.deleteMedicine(id); // Call API to delete medicine
      await loadMedicines(); // Reload medicines
      showMessage('success', 'Medicine deleted successfully');
    } catch (err) {
      showMessage('error', 'Failed to delete medicine');
    }
  };

  // Function to reset form and clear editing state
  const resetForm = () => {
    setIsAddingMedicine(false); // Turn off adding mode
    setIsEditing(false); // Turn off editing mode
    setNewMedicine({ name: '', manufacturer: '', expiryDate: '', price: 0, quantity: 0 }); // Clear form
    setEditingMedicine(null); // Clear editing state
    setMessage(null); // Clear message
  };

  // Function to show success or error message with auto-hide
  const showMessage = (type: 'success' | 'error', content: string) => {
    setMessage({ type, content });
    // Hide the message after 3 seconds
    setTimeout(() => {
      setMessage(null);
    }, 3000);
  };

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Medicine Management</h1>

      {/* Notification for success/error messages */}
      {message && (
        <div
          className={`mb-4 p-4 rounded ${
            message.type === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
          } flex items-center`}
        >
          {message.type === 'success' ? <CheckCircle className="mr-2" /> : <XCircle className="mr-2" />}
          <span>{message.content}</span>
        </div>
      )}

      {/* Add Medicine Button */}
      <div className="mb-4">
        <button
          onClick={() => setIsAddingMedicine(true)}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center"
        >
          <Plus className="mr-2" /> Add Medicine
        </button>
      </div>

      {/* Medicine Form for Adding or Editing */}
      {(isAddingMedicine || isEditing) && (
        <div className="mb-4 p-4 bg-gray-100 rounded">
          <h2 className="text-xl font-bold mb-2">
            {isEditing ? 'Edit Medicine' : 'Add New Medicine'}
          </h2>
          <input
            type="text"
            placeholder="Name"
            className="mb-2 p-2 w-full rounded border"
            value={newMedicine.name}
            onChange={(e) => setNewMedicine({ ...newMedicine, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Batch Number"
            className="mb-2 p-2 w-full rounded border"
            value={newMedicine.manufacturer}
            onChange={(e) => setNewMedicine({ ...newMedicine, manufacturer: e.target.value })}
          />
          <input
            type="date"
            placeholder="Expiry Date"
            className="mb-2 p-2 w-full rounded border"
            value={newMedicine.expiryDate}
            onChange={(e) => setNewMedicine({ ...newMedicine, expiryDate: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            className="mb-2 p-2 w-full rounded border"
            value={newMedicine.price}
            onChange={(e) => setNewMedicine({ ...newMedicine, price: parseFloat(e.target.value) })}
          />
          <input
            type="number"
            placeholder="Quantity"
            className="mb-2 p-2 w-full rounded border"
            value={newMedicine.quantity}
            onChange={(e) => setNewMedicine({ ...newMedicine, quantity: parseInt(e.target.value) })}
          />
          <button
            onClick={isEditing ? handleUpdateMedicine : handleAddMedicine}
            className={`bg-${isEditing ? 'yellow' : 'green'}-500 hover:bg-${isEditing ? 'yellow' : 'green'}-700 text-white font-bold py-2 px-4 rounded`}
          >
            {isEditing ? 'Update Medicine' : 'Save Medicine'}
          </button>
          <button onClick={resetForm} className="ml-2 text-gray-600 hover:text-gray-800">
            Cancel
          </button>
        </div>
      )}

      {/* Medicines List Table */}
      <div className="bg-white shadow overflow-hidden sm:rounded-lg">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Batch Number</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expiry Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Quantity</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {medicines.map((medicine) => (
              <tr key={medicine.id}>
                <td className="px-6 py-4 whitespace-nowrap">{medicine.name}</td>
                <td className="px-6 py-4 whitespace-nowrap">{medicine.manufacturer}</td>
                <td className="px-6 py-4 whitespace-nowrap">{medicine.expiryDate}</td>
                <td className="px-6 py-4 whitespace-nowrap">Tsh.{medicine.price.toFixed(2)}</td>
                <td className="px-6 py-4 whitespace-nowrap">{medicine.quantity}</td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <button
                    onClick={() => handleEditMedicine(medicine)}
                    className="text-indigo-600 hover:text-indigo-900 mr-2"
                  >
                    <Edit className="h-5 w-5" />
                  </button>
                  <button
                    onClick={() => handleDeleteMedicine(medicine.id)}
                    className="text-red-600 hover:text-red-900"
                  >
                    <Trash className="h-5 w-5" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MedicineManagement;
